<template>
  <div
    class="rounded-lg shadow-lg border border-slate-300 hover:scale-102 transition cursor-pointer"
  >
    <div>
      <img
        class="rounded-t-lg w-full"
        src="data:image/jpeg;base64,"
        alt=""
      />
    </div>
    <div class="p-6">
      <div class="bg-blue-500 w-fit text-white px-2 py-1 mb-4">
        <h1>{{ props.header }}</h1>
      </div>
      <p class="mb-6 text-[#353535]">
        {{ props.decription }}
      </p>
      <p class="text-sm">{{ props.createdAt }}</p>

      <button
        type="button"
        @click="updataActivity"
        class="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        แก้ไขข่าว
      </button>
      <button
        type="button"
        @click="OndeleteActivity"
        class="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-red-600"
      >
        ลบข่าว
      </button>
    </div>
  </div>
</template>

<script setup lang="ts">
import axios from "axios";
import { onMounted, ref } from "vue";
import { useRoute, useRouter } from "vue-router";
import type { activityDTO } from "../models/activity.dto";

const props = defineProps({
  id: Number,
  header: String,
  decription: String,
  type: String,
  createdAt: Date,
  updateAt: Date,
});

const router = useRouter();
const route = useRoute();
const id = route.query.id;

onMounted(() => {
  console.log("fetch  api");
  getAllActivitiesFromAPI();
});
const activities = ref<activityDTO[]>([]);

const getAllActivitiesFromAPI = async () => {
  try {
    const response = await axios.get("/api/v1/activities");
    activities.value = response.data.result;
    // console.log(activities.value);
  } catch (error) {
    console.log(error);
  }
};

const updataActivity = () => {
  router.push({
    path: "/update-activity",
    query: {
      id: props.id,
    },
  });
};
const emit = defineEmits(['delete'])
const OndeleteActivity = async () => {
 console.log("first",props.id)
 emit('delete',props.id)
 
};
</script>
